(143, 20, 'calcMenu1.cpp', 0, '// ------------------------------------------------------------------\r\n// File name:   calcMenu1.cpp\r\n// Assign ID:   LAB8\r\n// Due Date:    mm/dd/yy at hh \r\n//\r\n// Purpose:     Carry out a number of operations on 2 numbers\r\n//\r\n// Author:      yourUnixLogin Firstname Lastname\r\n//\r\n// ------------------------------------------------------------------\r\n\r\n\r\n#include <iostream>\r\nusing namespace std;\r\n\r\nvoid showChoices();\r\nfloat add(float, float);\r\nfloat subtract(float, float);\r\nfloat multiply(float, float);\r\nfloat divide(float, float);\r\n\r\nint main()\r\n{\r\n        float x, y;\r\n        int choice;\r\n\r\n        cout << endl << "(c) 2013, yourUnixLogin First Last" << endl << endl;\r\n        \r\n        cout << "This program is a menu driven program that does some calculations\\n"\r\n             << "and prints out the result as needed." << endl << endl;\r\n\r\n        do\r\n        {\r\n                showChoices();\r\n                cin >> choice;\r\n                switch (choice)\r\n                {\r\n                case 1:\r\n                        cout << "Enter two numbers: ";\r\n                        cin >> x >> y;\r\n                        cout << "Sum " << add(x,y) <<endl;\r\n                        break;\r\n                case 2:\r\n                        cout << "Enter two numbers: ";\r\n                        cin >> x >> y;\r\n                        cout << "Difference " << subtract(x,y) <<endl;\r\n                        break;\r\n                case 3:\r\n                        cout << "Enter two numbers: ";\r\n                        cin >> x >> y;\r\n                        cout << "Product " << multiply(x,y) <<endl;\r\n                        break;\r\n                case 4:\r\n                        cout << "Enter two numbers: ";\r\n                        cin >> x >> y;\r\n                        cout << "Quotient " << divide(x,y) <<endl;\r\n                