(155, 22, 'readNum.cpp', 0, '// ------------------------------------------------------------------\r\n// File name:   readNums.cpp\r\n// Assign ID:   LAB9\r\n// Due Date:    10/26/12 at 2:50pm\r\n//\r\n// Purpose:     Write a program that read in integers from a file and \r\n//              does some arithmetic on these numbers \r\n//                                                      \r\n// Author:      cop3014l\r\n// ------------------------------------------------------------------\r\n\r\n#include <iostream>\r\n#include <iomanip>\r\n#include<fstream>\r\n#include <string>\r\nusing namespace std;\r\n\r\nint main()\r\n{\r\n  // ----------------------------------------------------------------------\r\n    //  Declare variables\r\n    // -------------------------------------------------------------------\r\n        int variable,\r\n           tcount=0, \r\n           //ncount=0, \r\n           s_pos=0,\r\n           pos=0,\r\n           s_neg=0,\r\n           neg=0,\r\n           odd=0,\r\n           even=0, \r\n           sum=0;\r\n        float avgpos = 0,\r\n              avgneg = 0; \r\n   \r\n   //-| ----------------------------------------------------------------------\r\n   //-| Print the copyright notice declaring authorship.\r\n   //-| ----------------------------------------------------------------------\r\n\r\n        cout <<"(c) 2013, yourUnixLogin First Last\\n\\n"; \r\n\r\n        cout << "Statistics for the file ''readNums.in''." << endl << endl;\r\n\r\n   //-| ----------------------------------------------------------------------\r\n   //-| Algorithm Steps\r\n   //-| ----------------------------------------------------------------------\r\n         \r\n    ifstream  in;\r\n    in.open("readNums.in");\r\n\r\n        if(!in.is_open())\r\n        {\r\n                cout << "file ''readNums.in'' cannot open"<<endl; \r\n                return 0;        \r\n        }\r\n\r\n        while(!in.eof())\r\n        {\r\n                in >> variable; \r\n    