(256, 36, 'employeeStruc.cpp', 0, '// ------------------------------------------------------------------\r\n// File name:   employeeRecord.cpp\r\n// Assign ID:   LAB11\r\n// Due Date:    mm/dd/yy at hh \r\n//\r\n// Purpose:     Structs\r\n//\r\n// Author:      yourUnixLogin Firstname Lastname\r\n//\r\n// ------------------------------------------------------------------\r\n\r\n#include <iostream>\r\n#include <fstream>\r\nusing namespace std;\r\n\r\nstruct Employee\r\n{\r\n    string fname;\r\n    string lname;\r\n    int id;\r\n    int hours;\r\n    float rate;\r\n};\r\n\r\nvoid menu();\r\nvoid display(Employee);\r\n\r\nEmployee e1, e2, e3, e4, e5, e6;\r\n\r\nint main()\r\n{\r\n\r\n        cout << endl << "(c) 2013, yourUnixLogin First Last" << endl << endl; \r\n\r\n        //Open file and read in data\r\n        ifstream in;\r\n        int option;\r\n\r\n        in.open("employee.in");\r\n        if( in.fail() )\r\n        {\r\n           cout << "Cannot open file - employeee.in -" << endl << endl;\r\n           return -1;\r\n        }\r\n\r\n\r\n        // read in data\r\n        in >> e1.fname >> e1.lname >> e1.id >> e1.hours >> e1.rate;\r\n        in >> e2.fname >> e2.lname >> e2.id >> e2.hours >> e2.rate;\r\n        in >> e3.fname >> e3.lname >> e3.id >> e3.hours >> e3.rate;\r\n        in >> e4.fname >> e4.lname >> e4.id >> e4.hours >> e4.rate;\r\n        in >> e5.fname >> e5.lname >> e5.id >> e5.hours >> e5.rate;\r\n        in >> e6.fname >> e6.lname >> e6.id >> e6.hours >> e6.rate;\r\n\r\n        // Display menu wit hlist of last names\r\n        do\r\n        {\r\n                menu();\r\n                cout << "Please enter in an option: ";\r\n                cin >> option;\r\n                switch (option)\r\n                {\r\n                case 1: display(e1);\r\n                        break;\r\n                case 2: display(e2);\r\n                        break;\r\n                case 3: display(e3);\r\n                        break;\r\n                case 4: display(e4);\r\n                        break;\r\n                case 5: display(e5);\r\n                        break;\r\n                case 6: display(e6);\r\n                        break;\r\n                case 0: cout << "Exiting program" << endl;\r\n                        break;\r\n                }               \r\n        } while (option != 0);\r\n        \r\n        cout << endl << "(c) 2013, yourUnixLogin First Last" << endl << endl; \r\n\r\n\r\n        return 0;\r\n\r\n}//main\r\n\r\n \r\nvoid menu()\r\n{\r\n   cout << endl;\r\n   cout << "==========================================" << endl;\r\n   cout << "| Information menu for Employee data     |" << endl;\r\n   cout << "==========================================" << endl;\r\n   cout << "| 1) " << e1.lname << endl;\r\n   cout << "| 2) " << e2.lname << endl;\r\n   cout << "| 3) " << e3.lname << endl;\r\n   cout << "| 4) " << e4.lname << endl;\r\n   cout << "| 5) " << e5.lname << endl;\r\n   cout << "| 6) " << e6.lname << endl;\r\n   cout << "| " << endl;\r\n   cout << "| 0) Exit" << endl;\r\n   cout << "==========================================" << endl << endl;\r\n}\r\n\r\nvoid display(Employee a)\r\n{\r\n   cout << endl;\r\n   cout <<"===================================" << endl;\r\n   cout <<"Employee info : " << a.lname << endl;\r\n   cout <<"===================================" << endl;\r\n   cout <<"First Name  : " << a.fname << endl;\r\n   cout <<"Last Name   : " << a.lname << endl;\r\n   cout <<"Employee ID : " << a.id << endl;\r\n   cout <<"Hours       : " << a.hours << endl;\r\n   cout <<"Wage Rate   : " << a.rate << endl;\r\n   cout <<"Wages       : " << a.rate * a.hours << endl;\r\n   cout <<"===================================" << endl;\r\n}', 0, 0, 'source', 0),
